import React, { useState } from 'react'

function Paginator(props) {
  function handleClick(pageNumber) {
    props.onPaginatorClick(pageNumber)
  }

  const rows = []
  const {totalCount, perPage, currentPage} = props
  const availablePages = Math.ceil(totalCount / perPage)
  if (1 < availablePages < 10) {
    for (let i = 1; i <= availablePages; i++) {
      const pageNumber = i

      if (!(pageNumber > (currentPage + 2) || pageNumber < (currentPage - 2))) {
        rows.push(
          <div key={pageNumber}
            className={pageNumber === currentPage ?  'pageNumber active' : 'pageNumber'}
            onClick={() => handleClick(pageNumber)}>
            <p>{pageNumber}</p>
          </div>
        )
      }
    }
  }

  return (
    <div className='Paginator'>
      {rows}
    </div>
  )
}

export default Paginator
