import React, { useState, useEffect } from 'react'
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'
import Paginator from './Paginator.js'
const axios = require('axios')

// what new stuff i learned in this project:
// used hooks for the first time (state, effect, ref)
// used react router (with nested routes)
// used axios instead of fetch
// learned about local storage and session storage and how to use them

// TO DO
//what if no owner photo is provided
//folder architecture
//localstorage
//коммит и публикация на github pages
//поиск без кнопки, просто по введению текста

function Home(props) {
  const [result, setResult] = useState({repos: []})
  const [totalCount, setTotalCount] = useState(0)
  const [currentPage, setCurrentPage] = useState(1)
  const [preloader, setPreloader] = useState(false)
  const [userQuery, setUserQuery] = useState('')

  const defaultQuery = 'stars:>100'
  const perPage = '10'

  function handleSearch(value) {
    setUserQuery(value)
    setResult({repos: []})
    setPreloader(true)
    setCurrentPage(1)
  }

  function handlePaginatorClick(pageNumber) {
    setCurrentPage(pageNumber)
    setResult({repos: []})
  }

  useEffect(() => {
    sessionStorage.getItem('query')
    sessionStorage.getItem('page')
  }, [])

  useEffect(() => {
    sessionStorage.setItem('query', userQuery)
    sessionStorage.setItem('page', String(currentPage))
  }, [userQuery, currentPage])

  useEffect(() => {
    console.log(sessionStorage.getItem('query'));
    console.log(sessionStorage.getItem('page'));
    setPreloader(true)
    const query = (userQuery !== '') ? userQuery : defaultQuery
    const url = `https://api.github.com/search/repositories?q=${query}&sort=stars&order=desc&per_page=${perPage}&page=${currentPage}`
    const headers = { 'Authorization' : 'token 29e60afef909b3a9a1af5c6a80daee1da70ea57c' }

    axios.get(url, { 'headers': headers })
    .then(response => {
      setTotalCount(response.data.total_count)
      const items = response.data.items
      const repos = []
      const headers = { 'Authorization' : 'token 29e60afef909b3a9a1af5c6a80daee1da70ea57c' }

      items.map((item, index) => {
        function fetchAdditionalData() {
          function fetchLastCommitDate() {
            return axios.get(`https://api.github.com/repos/${item.owner.login}/${item.name}/commits`, { 'headers': headers })
          }

          function fetchLanguages() {
            return axios.get(item.languages_url, { 'headers': headers })
          }

          function fetchContributors() {
            return axios.get(item.contributors_url, { 'headers': headers })
          }

          Promise.all([fetchLastCommitDate(), fetchLanguages(), fetchContributors()])
          .then(function (results) {
            const [commitsData, languagesData, contributorsData] = results

            const lastCommitDate = new Date(commitsData.data[0].commit.committer.date).toLocaleDateString()

            const languages = Object.keys(languagesData.data)

            const parametres = ['login']
            let filteredContributors = contributorsData.data
            .filter((item, index) => index < 10)
            .map(item => {
              const contributor = {}
              Object.keys(item)
              .filter(key => parametres.includes(key))
              .map(key => contributor[key] = item[key])
              return contributor
            })

            const repo = {
              name:           item.name,
              stars:          item.stargazers_count,
              url:            item.html_url,
              lastCommitDate: lastCommitDate,
              languages:      languages,
              ownerName:      item.owner.login,
              ownerPhoto:     item.owner.avatar_url,
              ownerUrl:       item.owner.html_url,
              description:    item.description,
              contributors:   filteredContributors,
            }

            repos[index] = repo
            setResult({repos: repos})
            })
          }
        fetchAdditionalData()
      })
    })
    .then(() => setPreloader(false))

  }, [userQuery, currentPage])

  return (
    <div className='Home'>
      <SearchField onInputChange={handleSearch} />

      {userQuery !== ''
      ? <h1>Результаты поиска</h1>
      : <h1>10 самых популярных репозиториев</h1>}
      <ResultList repos={result.repos}
                  onResultItemClick={props.onResultItemClick}
                  preloader={preloader}
                  totalCount={totalCount} />

      {userQuery !== ''
      ? <Paginator currentPage={currentPage}
                   onPaginatorClick={handlePaginatorClick}
                   totalCount={totalCount}
                   perPage={perPage} />
      : null}
    </div>
  )
}

export default Home
