import React, { useState } from 'react'
import './App.css';
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'
import Paginator from './Paginator.js'

function App() {
  const defaultQuery = 'stars:>100'
  const [result, setResult] = useState({repos: []})
  const [query, setQuery] = useState('')
  const [activePage, setActivePage] = useState(1)
  // const [linkHeader, setlinkHeader] = ''

  function getLinks(linkHeader) {
    const parse = require('parse-link-header')
    const parsed = parse(linkHeader)
    console.log(parsed)
  }

  function getRepos(query) {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=3&page=1')
    .then(response => {
      getLinks(response.headers.get('Link'))
      return response.json()
    })
    .then(data => {
      console.log(data);
      setResult({repos: data.items})
    })
  }

  window.addEventListener('load', () => getRepos(defaultQuery))

  function handleSearchInputChange(value) {
    (value !== '') ? getRepos(value) : getRepos(defaultQuery)
  }

  return (
    <div className="App">
      <SearchField onInputChange={handleSearchInputChange} />
      <ResultList repos={result.repos} />
      <Paginator activePage={activePage} />
    </div>
  );
}

export default App
