import React, { useState } from 'react';

function SearchField() {
  const [query, setQuery] = useState('stars:>100')

  function handleChange(event) {
    const value = event.target.value
    setQuery(value)

    this.props.handleSearchInputChange(event)
    getRepos()
  }

  return (
    <div>
      <input placeholder="Поиск по репозиториям" onChange={handleChange}></input>
      <p>Результаты поиска</p>
      {result.repos.map(repo => {
        return (
          <div>
            <h2>{repo.name}</h2>
            <p>{repo.stargazers_count} звезд </p>
            <p>Последний коммит {repo.stargazers_count}</p>
            <p>{repo.url}</p>
          </div>
        )})}
      <div></div>
    </div>
  );
}

export default SearchField
