import React, { useState } from 'react';

function SearchField() {
  const [query, setQuery] = useState('stars:>100')
  const [result, setResult] = useState({repos: 'none yet'})

  function getRepos() {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=2&page=1')
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      console.log(data.items)
      setResult({repos: data.items})
    })
  }

  function handleChange(event) {
    const value = event.target.value
    setQuery(value)
    getRepos()
  }

  return (
    <div>
      <input placeholder="Поиск по репозиториям" onChange={handleChange}></input>
      <p>The repos are: </p>
      <div></div>
    </div>
  );
}

export default SearchField
