import React, { useState } from 'react'
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'
import Paginator from './Paginator.js'


//HANDLE NO RESULTS!!!
//PRELOADER
//what if no owner photo is provided

function Home(props) {
  const defaultQuery = 'stars:>100'
  const [result, setResult] = useState({repos: []})
  const [currentPage, setCurrentPage] = useState(1)

  const [query, _setQuery] = useState('')
  const queryRef = React.useRef(query)

  function setQuery(data) {
    queryRef.current = data
    _setQuery(data)
  }

  function handlePaginatorClick(pageNumber) {
    getData(queryRef.current, String(pageNumber))
    setCurrentPage(pageNumber)
  }

  function getData(query, page) {
    const url = `https://api.github.com/search/repositories?q=${query}&sort=stars&order=desc&per_page=10&page=${page}`
    const headers = { 'Authorization' : 'token 29e60afef909b3a9a1af5c6a80daee1da70ea57c' }

    fetch(url, { 'headers': headers })
    .then(response => response.json())
    .then(data => setRepos(data.items))
  }

  function setRepos(items) {
    const repos = []

    items.map((item, index) => {
      const headers = { 'Authorization' : 'token 29e60afef909b3a9a1af5c6a80daee1da70ea57c' }
      let gatherAdditionalData = getLastCommitDate(item)

      function getLastCommitDate(item) {
        const url = `https://api.github.com/repos/${item.owner.login}/${item.name}/commits`
        fetch(url, { 'headers': headers })
        .then(response => response.json())
        .then(data => new Date(data[0].commit.committer.date).toLocaleDateString())
        .then(date => getLanguages(item, date))
      }

      function getLanguages(item, date) {
        const url = item.languages_url
        fetch(url, { 'headers': headers })
        .then(response => response.json())
        .then(languages => getContributors(item, date, Object.keys(languages)))
      }

      function getContributors(item, date, languages) {
        const url = item.contributors_url
        fetch(url, { 'headers': headers })
        .then(response => response.json())
        .then(data => {
          const neededParametres = ['login', 'contributions']
          let filteredContributors = data
          .filter((item, index) => index < 10)
          .map(item => {
            const contributor = {}
            Object.keys(item)
            .filter(key => neededParametres.includes(key))
            .map(key => contributor[key] = item[key])
            return contributor
          })
          setRepo(item, date, languages, filteredContributors)
        })
      }
    })

    function setRepo(item, date, languages, contributors) {
      let repo = {
        name:           item.name,
        stars:          item.stargazers_count,
        url:            item.html_url,
        lastCommitDate: date,
        languages:      languages,
        ownerName:      item.owner.login,
        ownerPhoto:     item.owner.avatar_url,
        ownerUrl:       item.owner.html_url,
        description:    item.description,
        contributors:   contributors,
      }
      repos.push(repo)
      setResult({repos: repos})
    }
  }

  // window.addEventListener('load', () => getData(defaultQuery, '1'))

  function handleSearch(value) {
    setQuery(value);
    (value !== '') ? getData(value, '1') : getData(defaultQuery, '1')
  }

  return (
    <div className='Home'>
      <SearchField onInputChange={handleSearch} />
      <ResultList repos={result.repos} onResultItemClick={props.onResultItemClick} />
      <Paginator currentPage={currentPage} onPaginatorClick={handlePaginatorClick} />
    </div>
  );
}

export default Home
