import React, { useState } from 'react'
import './App.css';
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'
import Paginator from './Paginator.js'

//HANDLE NO RESULTS!!!

function App() {
  const defaultQuery = 'stars:>100'
  const [result, setResult] = useState({repos: []})
  const [activePage, setActivePage] = useState(1)

  const [query, _setQuery] = useState('')
  const queryRef = React.useRef(query)

  function setQuery(data) {
    queryRef.current = data
    _setQuery(data)
  }

  function handlePaginatorClick(pageNumber) {
    getData(queryRef.current, String(pageNumber))
    setActivePage(pageNumber)
  }

  function getData(query, page) {
    const url = 'https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=3&page=' + page
    const headers = { 'Authorization' : 'token 29e60afef909b3a9a1af5c6a80daee1da70ea57c' }

    fetch(url, { 'headers': headers })
    .then(response => {
      return response.json()
    })
    .then(data => {
      // console.log(getRepos(data.items)[0]);
      getRepos(data.items)
    })
  }


  function getRepos(items) {
    items.map(item => {
      const url = 'https://api.github.com/repos/' + item.owner.login + '/' + item.name + '/commits'
      const headers = { 'Authorization' : 'token 29e60afef909b3a9a1af5c6a80daee1da70ea57c' }
      let getLastCommitDate = fetch(url, { 'headers': headers })
      .then(response => response.json())
      .then(data => {
        repo.lastCommitDate = new Date(data[0].commit.committer.date).toLocaleDateString()
      })

      let repo = {
        name: item.name,
        stars: item.stargazers_count,
        url: item.html_url,
        lastCommitDate: getLastCommitDate,
      }

      let p = new Promise((resolve, reject) => {
        if (repo.lastCommitDate) {
          resolve(repo.lastCommitDate)
        } else {
          reject('Error' + repo.lastCommitDate)
        }
      })
      p.then((repo) => {
        console.log(repo)
      }).catch((error) => {
        console.log(error)
      })
    })

    // const repos

    // return await repos
    // setResult({repos: data})

  }







  window.addEventListener('load', () => getData(defaultQuery, '1'))

  function handleSearchInputChange(value) {
    setQuery(value);
    (value !== '') ? getData(value, '1') : getData(defaultQuery, '1')
  }

  return (
    <div className="App">
      <SearchField onInputChange={handleSearchInputChange} />
      <ResultList repos={result.repos} />
      <Paginator activePage={activePage} onPaginatorClick={handlePaginatorClick} />
    </div>
  );
}

export default App
