import React, { useState } from 'react'
import './App.css';
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'
import Paginator from './Paginator.js'

//HANDLE NO RESULTS!!!

function App() {
  const defaultQuery = 'stars:>100'
  const [result, setResult] = useState({repos: []})
  const [activePage, setActivePage] = useState(1)

  const [query, _setQuery] = useState('')
  const queryRef = React.useRef(query)

  function setQuery(data) {
    queryRef.current = data
    _setQuery(data)
  }

  function handlePaginatorClick(pageNumber) {
    getRepos(queryRef.current, String(pageNumber))
    setActivePage(pageNumber)
  }

  function getRepos(query, page) {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=3&page=' + page + '&access_token=29e60afef909b3a9a1af5c6a80daee1da70ea57c', {
      'headers': 'Authorization: token 29e60afef909b3a9a1af5c6a80daee1da70ea57c'
    })
    .then(response => {
      return response.json()
    })
    .then(data => {
      // getLastCommitDate(data.items)
      // console.log(data.items[0]);
      setResult({repos: data.items})
      return data.items
    })
  }

  // function getLastCommitDate(repos) {
  //   repos.map(repo => {
  //     fetch('https://api.github.com/repos/' + repo.owner.login + '/' + repo.name + '/commits'  + '&access_token=29e60afef909b3a9a1af5c6a80daee1da70ea57c')
  //     .then(response => {
  //       return response.json()
  //     })
  //     .then(data => {
  //       console.log(data[0].commit.committer.date);
  //       repo.lastCommitDate = data[0].commit.committer.date
  //     })
  //     // console.log(repo);
  //     // return repos
  //   })
  // }

  window.addEventListener('load', () => getRepos(defaultQuery, '1'))

  function handleSearchInputChange(value) {
    setQuery(value);
    (value !== '') ? getRepos(value, '1') : getRepos(defaultQuery, '1')
  }

  return (
    <div className="App">
      <SearchField onInputChange={handleSearchInputChange} />
      <ResultList repos={result.repos} />
      <Paginator activePage={activePage} onPaginatorClick={handlePaginatorClick} />
    </div>
  );
}

export default App
