import React, { useState } from 'react'
import './App.css';
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'
import Paginator from './Paginator.js'

function App() {
  const defaultQuery = 'stars:>100'
  const [result, setResult] = useState({repos: []})
  const [activePage, setActivePage] = useState(1)

  const [query, _setQuery] = useState('')
  const queryRef = React.useRef(query)

  function setQuery(data) {
    queryRef.current = data
    _setQuery(data)
  }

  function handlePaginatorClick(pageNumber) {
    getRepos(queryRef.current, String(pageNumber))
    setActivePage(pageNumber)
  }

  function getRepos(query, page) {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=3&page=' + page)
    .then(response => {
      return response.json()
    })
    .then(data => {
      setResult({repos: data.items})
      return data
    })
    .then(data => {
      console.log(data);
    })
  }

  window.addEventListener('load', () => getRepos(defaultQuery, '1'))

  function handleSearchInputChange(value) {
    setQuery(value);
    (value !== '') ? getRepos(value, '1') : getRepos(defaultQuery, '1')
  }

  return (
    <div className="App">
      <SearchField onInputChange={handleSearchInputChange} />
      <ResultList repos={result.repos} />
      <Paginator activePage={activePage} onPaginatorClick={handlePaginatorClick} />
    </div>
  );
}

export default App
