import React, { useState } from 'react'
import './App.css';
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'

function App() {
  const [result, setResult] = useState({repos: []})

  function getRepos(query) {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=10&page=1')
    .then(response => response.json())
    .then(data => setResult({repos: data.items}))
  }

  function handleSearchInputChange(value) {
    getRepos(value)
  }

  return (
    <div className="App">
      <SearchField onInputChange={handleSearchInputChange} />
      <ResultList repos={result.repos} />
    </div>
  );
}

export default App
