import React, { useState } from 'react';

function SearchField() {
  const [query, setQuery] = useState('stars:>100')
  const [result, setResult] = useState({repos: []})

  function getRepos() {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=2&page=1')
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      console.log(data)
      setResult({repos: data.items})
    })
  }

  function handleChange(event) {
    const value = event.target.value
    setQuery(value)
    getRepos()
  }

  return (
    <div>
      <input placeholder="Поиск по репозиториям" onChange={handleChange}></input>
      <p>The repos are: </p>
      {result.repos.map(repo => {
        return (
          <div>
            <h2>{repo.name}</h2>
            <p>{repo.stargazers_count} звезд </p>
            <p>Дата последнего коммита {repo.stargazers_count}</p>
            <p>{repo.url}</p>
          </div>
        )})}
      <div></div>
    </div>
  );
}

export default SearchField
