import React, { useState } from 'react';
import './App.css';
import SearchField from './SearchField.js'
import ResultList from './ResultList.js'

function App() {
  const [result, setResult] = useState({repos: []})

  function getRepos() {
    fetch('https://api.github.com/search/repositories?q=' + query + '&sort=stars&order=desc&per_page=10&page=1')
    .then((response) => {
      return response.json()
    })
    .then((data) => {
      console.log(data)
      setResult({repos: data.items})
    })
  }

  function handleChange(event) {
    const value = event.target.value
    setQuery(value)
    getRepos()
  }

  return (
    <div className="App">
      <SearchField />

      <ResultList repos={result.repos}/>

      <p>Paginator</p>
    </div>
  );
}

export default App;
