import React, { useState } from 'react'

function SearchField(props) {
  const queryRef = React.useRef(null)

  function handleChange(e) {
    const value = e.target.value
    props.onInputChange(value)
  }

  const { storedQuery, userQuery } = props
  // let defaultQuery
  // if (storedQuery) {
  //   defaultQuery = storedQuery
  // } else if (userQuery.length > 0) {
  //   defaultQuery = userQuery
  // }
  // const defaultQuery = storedQuery ? storedQuery : userQuery

  return (
    <div className='SearchField'>
      <input ref={queryRef}
             onChange={handleChange}
             placeholder={'Start typing repository name'}
             defaultValue={storedQuery ? storedQuery : ''} >
      </input>
    </div>
  )
}

export default SearchField
